import React, { useState } from 'react';
import './CSS/LoginSignup.css';
import { registerUser } from '../ic/ic-service';

const LoginSignup = () => {
  const [isChecked, setIsChecked] = useState(false);
  const [name, setName] = useState('');
  const [message, setMessage] = useState(''); // State to store messages
  const [error, setError] = useState(''); // State to store error messages

  const handleCheckboxChange = (event) => {
    setIsChecked(event.target.checked);
  };

  const handleInputChange = (event) => {
    setName(event.target.value);
  };

  const handleSubmit = async () => {
    if (isChecked && name) {
      try {
        const user = await registerUser(name);
        if (user) {
          setMessage('User registered successfully!'); // Success message
          setError(''); // Clear any previous error
        } else {
          setMessage('');
          setError('User already exists!'); // Error message
        }
      } catch (error) {
        setMessage('');
        setError('There was an error registering the user. Please try again.'); // General error message
      }
    }
  };

  return (
    <div className='loginsignup'>
      <div className="loginsignup-container">
        <h1>Sign In</h1>
        <div className="loginsignup-fields">
          <input 
            type="text" 
            placeholder='Your Full Name' 
            value={name}
            onChange={handleInputChange}
          />
        </div>
        <button
          style={{
            opacity: isChecked ? 1 : 0.5,
            cursor: isChecked ? 'pointer' : 'not-allowed'
          }}
          disabled={!isChecked || !name}
          onClick={handleSubmit}
        >
          Continue
        </button>
        {message && <p className="loginsignup-message">{message}</p>} {/* Display success message */}
        {error && <p className="loginsignup-error">{error}</p>} {/* Display error message */}
        <div className="loginsignup-agree">
          <input
            type="checkbox"
            name=''
            id=''
            onChange={handleCheckboxChange}
          />
          <p>By continuing, I agree to the terms of use & privacy policy.</p>
        </div>
      </div>
    </div>
  );
};

export default LoginSignup;
