import React, { useState } from 'react';
import './CSS/LoginSignup.css';
// import { checkUser } from '../ic/ic-service';
import { useNavigate } from 'react-router-dom';

const Login = ({ onLogin }) => {
  const [name, setName] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleInputChange = (event) => {
    setName(event.target.value);
  };

  const handleSubmit = async () => {
    if (name) {
      const user = await checkUser(name);
      if (user) {
        onLogin(name);
        navigate('/'); // Redirect to the shop page
      } else {
        setError(`${name} does not exist! Please sign up first.`);
      }
    }
  };

  return (
    <div className='loginsignup'>
      <div className="loginsignup-container">
        <h1>Log In</h1>
        <div className="loginsignup-fields">
          <input 
            type="text" 
            placeholder='Your Full Name' 
            value={name}
            onChange={handleInputChange}
          />
        </div>
        <button onClick={handleSubmit}>
          Continue
        </button>
        {error && <p className="loginsignup-error">{error}</p>} {/* Display error message */}
      </div>
    </div>
  );
};

export default Login;
