import React, { createContext, useState } from "react";
import all_product from "../Components/Assets/all_product";

export const ShopContext = createContext(null);

const getDefaultCart = () => {
    let cart = {};
    for (let index = 0; index < all_product.length + 1; index++) {
        cart[index] = 0;
    }
    return cart;
};

const ShopContextProvider = (props) => {
    const [cartItems, setCartItems] = useState(getDefaultCart());
    const [currency, setCurrency] = useState('USD'); // Default currency
    const [currencySymbol, setCurrencySymbol] = useState('$'); // Default symbol

    // Currency Symbols Map
    const currencySymbols = {
        USD: '$',
        EUR: '€',
        GBP: '£',
        BTC: '₿',
        ETH: 'Ξ',
        ICP: 'ICP', // Placeholder for ICP, adjust as needed
    };

    // Mock conversion rates (as an example, you should update these with real data)
    const conversionRates = {
        USD: 1,     // Base currency
        EUR: 0.85,
        GBP: 0.75,
        BTC: 0.00003,
        ETH: 0.0004,
        ICP: 0.8,
    };

    const convertPrice = (price, targetCurrency) => {
        const basePriceInUSD = price / conversionRates[currency]; // Convert to USD first
        const convertedPrice = basePriceInUSD * conversionRates[targetCurrency];
        return convertedPrice.toFixed(2); // Returning a fixed number of decimal places
    };

    const handleCurrencyChange = (newCurrency) => {
        setCurrency(newCurrency);
        setCurrencySymbol(currencySymbols[newCurrency]);
    };

    const addToCart = (itemId) => {
        setCartItems((prev) => ({ ...prev, [itemId]: prev[itemId] + 1 }));
        console.log(cartItems);
    };

    const removeFromCart = (itemId) => {
        setCartItems((prev) => ({ ...prev, [itemId]: prev[itemId] - 1 }));
    };

    const getTotalCartAmount = () => {
        let totalAmount = 0;
        for (const item in cartItems) {
            if (cartItems[item] > 0) {
                let itemInfo = all_product.find((product) => product.id === Number(item));
                totalAmount += cartItems[item] * convertPrice(itemInfo.new_price, currency);
            }
        }
        return totalAmount;
    };

    const getTotalCartItems = () => {
        let totalItem = 0;
        for (const item in cartItems) {
            if (cartItems[item] > 0) {
                totalItem += cartItems[item];
            }
        }
        return totalItem;
    };

    const contextValue = {
        getTotalCartItems,
        getTotalCartAmount,
        all_product,
        cartItems,
        addToCart,
        removeFromCart,
        currency,
        currencySymbol,
        handleCurrencyChange,
        convertPrice,
    };

    return (
        <ShopContext.Provider value={contextValue}>
            {props.children}
        </ShopContext.Provider>
    );
};

export default ShopContextProvider;
